mapper.update:
UPDATE CART SET
    AMOUNT=#{amount}
where
    USERID=#{userid} and PRODUCTID=#{productid}

mapper.delete:
DELETE
FROM CART
WHERE
    USERID=#{userid}
  AND
    PRODUCTID=#{id}

mapper.update:
UPDATE PRODUCT SET
    AMOUNT=AMOUNT-#{amount}
WHERE ID=#{id}

mapper.select:
SELECT id,userid,name,phone,postcode,address,addressdetail,memo,taxagree,indate,status FROM ORDERBOOK
WHERE USERID=#{userid}
ORDER BY INDATE DESC
